import React, { useState, useEffect } from 'react';
import './App.css';
import ZibbyBlob from './ZibbyBlob';
import IconButton from './IconButton';
import { FaUser, FaComments, FaBook, FaUsers, FaRegCalendarAlt } from 'react-icons/fa';

function App() {
  const icons = [
    { label: "Me", icon: <FaUser />, notification: true },
    { label: "ZibLine", icon: <FaComments /> },
    { label: "Memories", icon: <FaBook />, badgeCount: 3 },
    { label: "Family & Friends", icon: <FaUsers />, badgeCount: 2 },
    { label: "Journal", icon: <FaRegCalendarAlt /> },
  ];

  const [mood, setMood] = useState('calm');

  useEffect(() => {
    const moods = ['calm', 'happy', 'excited', 'reflective', 'sad'];
    const interval = setInterval(() => {
      const next = moods[Math.floor(Math.random() * moods.length)];
      setMood(next);
    }, 8000); // change mood every 8 seconds
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="app-container">
      <h1>Zibby</h1>
      <div className="centerpiece">
        <div className="icon-circle-array">
          {icons.map((props, i) => (
            <div
              className="icon-orbit"
              key={props.label}
              style={{
                transform: `rotate(${(360 / icons.length) * i}deg) translate(0, -19vw) rotate(-${(360 / icons.length) * i}deg)`
              }}
            >
              <IconButton {...props} />
            </div>
          ))}
          <div className="blob-center">
            <ZibbyBlob mood={mood} />
          </div>
        </div>
      </div>
      <div className="particles">
        {[...Array(mood === 'excited' ? 35 : 20)].map((_, i) => (
          <div
            key={i}
            className="particle"
            style={{
              '--i': i,
              '--x': Math.random(),
              '--y': Math.random()
            }}
          />
        ))}
      </div>
    </div>
  );
}

export default App;
