// App.js
import React from 'react';
import './App.css';
import ZibbyBlob from './ZibbyBlob';
import IconButton from './IconButton';
import { FaUser, FaComments, FaBook, FaUsers, FaRegCalendarAlt, FaBookOpen, FaStream } from 'react-icons/fa';

function App() {
  const icons = [
    { label: "Me", icon: <FaUser />, notification: true },
    { label: "ZibLine", icon: <FaStream /> },
    { label: "Memories", icon: <FaBook />, badgeCount: 3 },
    { label: "Family & Friends", icon: <FaUsers />, badgeCount: 2 },
    { label: "Journal", icon: <FaBookOpen /> },
  ];

  const ORBIT_RADIUS = 224; // Increased orbit radius by 40%

  return (
    <div className="app-container">
      <h1>Zibby</h1>
      <div className="centerpiece">
        <ZibbyBlob />
        <div className="icon-orbit-array">
          {icons.map((props, i) => (
            <div
              className="icon-orbit"
              key={props.label}
              style={{
                transform: `
                  rotate(${(360 / icons.length) * i}deg)
                  translate(0, -224px)
                  rotate(-${(360 / icons.length) * i}deg)
                `
              }}
            >
              <IconButton {...props} />
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default App;
